                    html    : $('<i />', {
                        'class' : 'fas fa-check'
                    })
            });
            
            $('#notification-container').show().append(div.fadeIn(duration||0));
            if (autohide) remove(div, delay, duration);
        }

        function remove(div, delay, duration) {
            div.delay(delay||0).fadeOut(duration || 0, function() {
                $(this).remove();
            });
        }
