        var ajaxKill = false;

        $(document).on('click', "#search-button", function (e) {
            e.preventDefault();
            var $searchBtn = $(this);
            var $searchInput = $('#search-input');
            var keyword = $searchInput.val().trim();

            var type = $('.search-content .nav-tabs li.active').find('a').attr('aria-controls');

            if (keyword !== "") {
                if (ajaxKill === false) {
                    ajaxKill = true;
