                    $searchInput.prop('disabled', true);

                    dataLayer.push({
                        'event' : 'search',
                        'keyword' : keyword.toLowerCase()
                    });

                    $.ajax({
                        url: "https://www.idntimes.com/search",
                        type: "GET",
                        data: {
                            keyword: keyword,
                            type: 'all',
