                        $searchBtn.prop('disabled', false);
                        $searchInput.prop('disabled', false);
                    });
                } else {
                    $('#search-list .loader').remove();
                    ajaxKill = false;
                    $searchBtn.prop('disabled', false);
                    $searchInput.prop('disabled', false);
                }
            }
